    fun toStringedByteArray(stream: InputStream): String {
        val str = StringBuilder("byteArrayOf(")
        while (stream.available() > 0) {
            str.append("0x${stream.read().toString(16).toUpperCase()}.toByte()")
            if (stream.available() > 0) {
                str.append(", ")
            }
        }
        str.append(")")
        return str.toString()
    }
    
